import { createContext, useState } from "react";

export const IpContext = createContext();

export const IpProvider = ({ children }) => {
  const [location, setLocation] = useState(null);
  const [error, setError] = useState("");
  const { ip, setIP } = useState(null);

  const fetchLocationData = async () => {
    setError("");
    try {
      const response = await fetch(
        // `http://localhost:3001/ipaddress/country,city?ipAddress=${ip}`
        `http://localhost:3001/ipaddress/country,city?ipAddress=8.8.8.8`
      );
      const data = await response.json();
      console.log(data)

      if (response.ok) {
        setLocation(data);
        console.log(location)
      } else {
        setError(data.error);
      }
    } catch (error) {
      console.error("Error:", error);
      setError("An error occurred while fectching data");
    }
  };
  const handleSearch = async(e)=> {
    e.preventDefault();
    await fetchLocationData();
  };

  return(
    <IpContext.Provider value ={{ip,setIP,location,error,handleSearch}}>{children}</IpContext.Provider>
  )
};
