import { useState } from "react";
import "./App.css";
import Address from "./components/Address";
import BannerArea from "./components/BannerArea";
import MapArea from "./components/MapArea";
import { MyContext } from "./IpContext";

function App() {
  const [location, setLocation] = useState(null);
  const [error, setError] = useState("");
  console.log(location)
  return (
    <div className="container">
      <MyContext.Provider value={{location,setLocation}}>
      <BannerArea setLocation={setLocation} setError={setError}/>
      <Address setLocation={location} />
      {location && <MapArea location={location} latitude={location.latitude} longitude={location.longitude}/>}
      {error && <p> {error}</p>}
      </MyContext.Provider>
    </div>
  );
}

export default App;
