import React, { useState } from "react";

function BannerArea() {
    const [search,setSearch] = useState()

    const fetchOptions = useCallback(async () => {
        await fetch(`http://localhost:3001/cities/cities?namePrefix=${search}`)
          .then((response) => response.json())
          .then((data) => {
            if (data.data) setOptions(data.data);
            console.log(data.data);
          })
    
          .catch((error) => {
            console.log("error", error);
          });
      }, [search]);

    const handleSearch = (e) => {
        setSearch(e.target.value);
    }
  return (
    <div
      className="banner-container"
      style={{
        background: "url(/assets/images/pattern-bg-desktop.png)",
        backgroundPosition: "center top",
      }}
    >
      <h1>IP Address Tracker</h1>
      <form>
        <input type="text" name="ipaddress" id="ipaddress" value={search} onChange={handleSearch} />
        <button className="input-button">&gt;</button>
      </form>
    </div>
  );
}

export default BannerArea;
