import { useState } from "react";
import "./App.css";
import Address from "./components/Address";
import BannerArea from "./components/BannerArea";
import MapArea from "./components/MapArea";
import { IpProvider } from "./IpContext";

function App() {
  {error && <p>{error}</p>}


  return (
    <div className="container">
     <IpProvider>
      <BannerArea />
      <Address  />
      {location && <MapArea location={location} latitude={location.latitude} longitude={location.longitude}/>}
      {error && <p> {error}</p>}
      </IpProvider>
    </div>

  );
}

export default App;
